mapper.update:
UPDATE NOTICE 
SET READCOUNT=READCOUNT+1 
WHERE NUM=#{id}

mapper.select:
SELECT NUM, SUBJECT, CONTENT, TO_CHAR(REGDATE, 'YYYY-MM-DD DAY PM HH:MI:SS') REGDATE, READCOUNT, WRITER, IP, ID, CATEGORY  
FROM NOTICE 
WHERE NUM=#{id}

mapper.update:
UPDATE NOTICE 
SET SUBJECT=#{subject}, CONTENT=#{content}, CATEGORY=#{category} 
WHERE NUM=#{num}
